import React, { Component, Fragment } from "react";
import Game from "./components/Game";

let s = false;

if (localStorage.getItem("game").length) {
  let json = JSON.parse(localStorage.getItem("game"));
  if (json.status)
    s = true;
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      status: s,
      isConfigOpen: s
    };
  }

  handleStart = () => {
    localStorage.setItem("game", JSON.stringify({"status": true}));
    this.setState({status: true});
  };

  handleExit = () => {
    localStorage.setItem("game", JSON.stringify({"status": false}));
    this.setState({status: false});
  };

  handleOpenConfig = () =>
    this.setState({isConfigOpen: true});

  renderFirstPage = () =>
    <div className="first-page">
      <div className="first-page__title">N-PUZZLE</div>
      <div className="first-page__subtitle">Venha se divertir</div>
      <div>
        <button onClick={this.handleStart}>Começar</button>
        <button onClick={this.handleOpenConfig}>Configurações</button>
      </div>
    </div>;

  renderConfig = () =>
    <div>
      <div>Confugurações</div>
      <div>
        <label>Nível:</label>
        <select>
          <option>Fácil</option>
          <option>Médio</option>
          <option>Difícil</option>
        </select>
      </div>
    </div>;

  render() {
    const {status, isConfigOpen} = this.state;

    return (
      <Fragment>
        {isConfigOpen && this.renderConfig()}
        {status
          ? <Game onExit={this.handleExit}/>
          : this.renderFirstPage()
        }
      </Fragment>
    );
  }
}

export default App;
