import React from "react";
import {PUZZLE_MODE_EASY} from "../../utils/constants";
import "../../index.css";

import Board from "../Board";
import {getPositionZero} from "../../utils";
class Game extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        steps: 0
      };
  }

  handleClickSquare = (value, position) => {
      if (value === 0) {
        return;
      }

      let squares = this.state.squares;
      const zero = getPositionZero(squares);

      if ((position.i === zero.i && Math.abs(position.j - zero.j) === 1)
      || (position.j === zero.j && Math.abs(position.i - zero.i) === 1)) {
        squares[position.i][position.j] = 0;
        squares[zero.i][zero.j] = value;

        this.setState(state => ({
          squares,
          steps: state.steps + 1
        }));
      }
  };

  handleReset = () => {
    this.setState(state => ({
      squares: shuffle(Puzzles[PUZZLE_MODE_EASY]),
      steps: 0
    }));
  };

  render() {
      const {steps} = this.state;
      const {onExit, onSaveAndExit, squares} = this.props;

      return (
        <div className="game-window">
          <div style={{textAlign: "center", marginBottom: 10, fontSize: 18}}>
            <div style={{marginBottom: 5}}>Passos</div>
            <div>{steps}</div>
          </div>
          <div className="game">
            <div className="game-board">
              <Board
                squares={squares}
                onClick={this.handleClickSquare}
              />
            </div>
          </div>
          <div style={{marginTop: 10}}>
            <button onClick={() => this.handleReset()}>Reiniciar</button>
            <button onClick={() => onSaveAndExit(squares)}>Salvar e Sair</button>
            <button onClick={() => onExit()}>Sair</button>
          </div>
        </div>
      );
  }
}

export default Game;
