import React from "react";
import "./index.css";

import Board from "./components/Board";

function shuffle(squares) {
    const m = squares.length;
    let t;
    for (let i = 0; i < m; i++) {
      let n = squares[i].length, k = Math.floor(Math.random() * i), l;
      for (let j = 0; j < n; j++) {
        l = Math.floor(Math.random() * j);
        t = squares[i][j];
        squares[i][j] = squares[k][l];
        squares[k][l] = t;
      }
    }
    return squares;
}

function getPositionZero(squares) {
  let position = {}, l = squares.length;
  for (let i = 0; i < l; i++)
    for (let j = 0; j < l; j++) {
      if (squares[i][j] === 0)
        position = {i, j};
    }

  return position;
}

const s = [
  [0, 1, 2],
  [3, 4, 5],
  [6, 7, 8]
];

class Game extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        squares: shuffle(s),
        zero: {i: null, j: null},
        steps: 0
      };
  }

  componentDidMount() {
    this.setState(state => ({
      ...state,
      zero: getPositionZero(state.squares)
    }))
  }

  handleClickSquare = (value, position) => {
      if (value === 0) {
        return;
      }

      let
        squares = this.state.squares,
        l = squares.length,
        pZero = {}
      ;

      console.log("0: ", pZero);
      console.log(`${value}: `, position);

      squares[position.i][position.j] = 0;
      squares[pZero.i][pZero.j] = value;

      this.setState(state => ({
          squares,
          stepNumber: state.stepNumber++
      }));
  };

  render() {
      const {squares, zero} = this.state;

      return zero.i !== null && zero.j !== null && (
          <div className="game">
            <div className="game-board">
                <Board 
                    squares={squares} 
                    onClick={this.handleClickSquare}
                />
            </div>
          </div>
      );
  }
}

export default Game;
