import React from "react";
import "./index.css";

import Board from "./components/Board";

function shuffle(array) {
    var m = array.length, t;
    for (let i = 0; i < m; i++) {
      let n = m[i].length, k = Math.floor(Math.random() * i), l;
      for (let j = 0; j < n; j++) {
        l = Math.floor(Math.random() * j);
        t = array[m][n];
        array[m][n] = array[i][j];
        array[i][j] = t;
      }
    }
    while (m) {
        i = Math.floor(Math.random() * m--);
        var n = array[m].length, j;
        while (n) {
          j = Math.floor(Math.random() * n--);

        }
    }
    return array;
}

const s = [
  [0, 1, 2],
  [3, 4, 5],
  [6, 7, 8]
];

class Game extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        squares: shuffle(s),
        stepNumber: 0
      };
  }

  handleClickSquare = (e, value) => {
      if (value === 0) {
        e.preventDefault();
        return false;
      }

      let squares = this.state.squares;

      /*for (let i = 0; i < squares.length/3; i++) {
          for (let j =  0; j < squares.length/3; j++) {
              console.log(`${i} >>>> ${j}`)
          }
      }*/

      //0: => 1 ou 3
      //1: => 0 ou 2 ou 4
      //2: => 1 ou 5
      //3: => 0 ou 4 ou 6
      //4: => 1 ou

      squares.map((a, i) => {
          if (a === 0) {
            squares[i] = value;
          }

          if (a === value) {
            squares[i] = 0;
          }
      });

      this.setState({squares, stepNumber: this.state.stepNumber++});
  };

  render() {
      const {squares} = this.state;
      return (
          <div className="game">
            <div className="game-board">
                <Board 
                    squares={squares} 
                    onClick={this.handleClickSquare}
                />
            </div>
          </div>
      );
  }
}

export default Game;
