import React from "react";
import {PUZZLE_MODE_EASY} from "../../utils/constants";
import "../../index.css";

import Board from "../Board";

function shuffle(squares) {
    const m = squares.length;
    let t;
    for (let i = 0; i < m; i++) {
      let n = squares[i].length, k = Math.floor(Math.random() * i), l;
      for (let j = 0; j < n; j++) {
        l = Math.floor(Math.random() * j);
        t = squares[i][j];
        squares[i][j] = squares[k][l];
        squares[k][l] = t;
      }
    }
    return squares;
}

function getPositionZero(squares) {
  let position = {}, l = squares.length;
  for (let i = 0; i < l; i++)
    for (let j = 0; j < l; j++) {
      if (squares[i][j] === 0)
        position = {i, j};
    }

  return position;
}

class Game extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        squares: shuffle(Puzzles[PUZZLE_MODE_EASY]),
        steps: 0
      };
  }

  handleClickSquare = (value, position) => {
      if (value === 0) {
        return;
      }

      let squares = this.state.squares;
      const zero = getPositionZero(squares);

      if ((position.i === zero.i && Math.abs(position.j - zero.j) === 1)
      || (position.j === zero.j && Math.abs(position.i - zero.i) === 1)) {
        squares[position.i][position.j] = 0;
        squares[zero.i][zero.j] = value;

        this.setState(state => ({
          squares,
          steps: state.steps + 1
        }));
      }
  };

  handleReset = () => {
    this.setState(state => ({
      squares: shuffle(Puzzles[PUZZLE_MODE_EASY]),
      steps: 0
    }));
  };

  render() {
      const {steps} = this.state;
      const {onExit, onSaveAndExit, squares} = this.props;

      return (
        <div className="game-window">
          <div style={{textAlign: "center", marginBottom: 10, fontSize: 18}}>
            <div style={{marginBottom: 5}}>Passos</div>
            <div>{steps}</div>
          </div>
          <div className="game">
            <div className="game-board">
              <Board
                squares={squares}
                onClick={this.handleClickSquare}
              />
            </div>
          </div>
          <div style={{marginTop: 10}}>
            <button onClick={() => this.handleReset()}>Reiniciar</button>
            <button onClick={() => onSaveAndExit(squares)}>Salvar e Sair</button>
            <button onClick={() => onExit()}>Sair</button>
          </div>
        </div>
      );
  }
}

export default Game;
