import React, { Component } from "react";
import Game from "./components/Game";

if (localStorage.getItem("game").length) {
  let json = JSON.parse(localStorage.getItem("game"));
  if (json.status)
    this.setState({isConfigOpen: true});
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      status: false,
      isConfigOpen: false
    };
  }

  componentDidMount() {
    if (localStorage.getItem("game").length) {
      let json = JSON.parse(localStorage.getItem("game"));
      if (json.status)
        this.setState({isConfigOpen: true});
    }
  }

  handleStartGame = () => {
    localStorage.setItem("game", JSON.stringify({"status": true}));
    this.setState({status: true});
  };

  handleOpenConfig = () =>
    this.setState({isConfigOpen: true});

  renderFirstPage = () =>
    <div>
      <div>N-PUZZLE</div>
      <div>Venha se divertir</div>
      <div>
        <button onClick={this.handleStartGame}>Começar</button>
        <button onClick={this.handleOpenConfig}>Configurações</button>
      </div>
    </div>;

  render() {
    const {status, isConfigOpen} = this.state;
    console.log(isConfigOpen);
    return status
      ? <Game/>
      : this.renderFirstPage();
  }
}

export default App;
