import React, { Fragment } from "react";
import "../../index.css";

import Board from "../Board";

class Game extends React.Component {
  render() {
    const {onExit, winner, onSaveAndExit, onReset, onClickSquare, squares, steps} = this.props;
    let
      a = [[1,2,3],[4,5,6],[7,8,0]],
      counter = 0;

    const le = (squares.length * (squares[squares.length - 1].length));

    for(let i = 0;i < squares.length;i++)
      for(let j = 0;j < squares[i].length; j++)
        if(squares[i][j] === a[i][j])
          counter++;

    if (counter < le)
      counter = 0;
    if (counter === le) {
      winner = true;
    }

    return (
      <Fragment>
        <div className="game-window">
          <div style={{textAlign: "center", marginBottom: 10, fontSize: 18}}>
            <div style={{marginBottom: 5}}>Passos</div>
            <div>{steps}</div>
          </div>
          <div className="game">
            <div className="game-board">
              <Board
                squares={squares}
                onClick={onClickSquare}
              />
            </div>
          </div>
          <div style={{marginTop: 10}}>
            <button onClick={() => onReset()}>Reiniciar</button>
            {/*<button onClick={() => onSaveAndExit(squares)}>Salvar e Sair</button>*/}
            <button onClick={() => onExit()}>Sair</button>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default Game;
