const
  STORAGE_DATABASE = "game",
  DAO_STATUS_CONNECTED_OK = 1,
  DAO_STATUS_CONNECTED_ERROR = -1,
  DAO_ID_SIZE = 8;

export
  const Storage = {
    response: () => ({status: 1, message: ""}),
    connect: () => {
      if (!("localStorage" in window)) {
        alert("Erro ao conectar!");
        return;
      }

      Storage.createDB();
    },
    createDB: () => {
      if (localStorage.getItem("game").length > 0)
        return;

      localStorage.setItem(STORAGE_DATABASE, Data.push([]))
    },
    insert: (game) => localStorage.setItem(STORAGE_DATABASE, Data.push(game)),
    update: () => {},
    delete: () => {}
  };

const Data = {
  push: (data) => [JSON.stringify(data)],
  pull: (data) => JSON.parse(data)[0]
};
