import React from "react";
import "./index.css";

import Board from "./components/Board";

function shuffle(array) {
    var m = array.length, t;
    for (let i = 0; i < m; i++) {
      let n = array[i].length, k = Math.floor(Math.random() * i), l;
      for (let j = 0; j < n; j++) {
        l = Math.floor(Math.random() * j);
        t = array[i][j];
        array[i][j] = array[k][l];
        array[k][l] = t;
      }
    }
    return array;
}

const s = [
  [0, 1, 2],
  [3, 4, 5],
  [6, 7, 8]
];

class Game extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        squares: shuffle(s),
        stepNumber: 0
      };
  }

  handleClickSquare = (value, position) => {
      if (value === 0) {
        return;
      }

      let
        squares = this.state.squares,
        l = squares.length,
        pActive = {},
        pZero = {}
      ;

      for (let i = 0; i < l; i++)
        for (let j = 0; j < l; j++) {
          if (squares[i][j] === value)
            pActive = {i,j};

          if (squares[i][j] === 0)
            pZero = {i,j};
        }

      console.log("0: ", pZero);
      console.log(`${value}: `, pActive);

      squares[pActive.i][pActive.j] = 0;
      squares[pZero.i][pZero.j] = value;

      this.setState(state => ({
          squares,
          stepNumber: state.stepNumber++
      }));
  };

  render() {
      const {squares} = this.state;
      return (
          <div className="game">
            <div className="game-board">
                <Board 
                    squares={squares} 
                    onClick={this.handleClickSquare}
                />
            </div>
          </div>
      );
  }
}

export default Game;
