import React, {Fragment} from "react";
import "../../index.css";

import Board from "../Board";

class Game extends React.Component {
  render() {
    const {onExit, onSaveAndExit, onReset, onClickSquare, squares, steps} = this.props;
    let
      a = [[1,2,3],[4,5,6],[7,8,0]],
      counter = 0,
      w = false;

    const le = (squares.length * (squares[squares.length - 1].length));

    for(let i = 0;i < squares.length;i++)
      for(let j = 0;j < squares[i].length; j++)
        if(squares[i][j] === a[i][j])
          counter++;

    if (counter < le)
      counter = 0;
    if (counter === le) {
      w = true;
    }
    return (
      <div className="game-window">
        <div style={{textAlign: "center", marginBottom: 10, fontSize: 18}}>
          {!w ? (
            <Fragment>
              <div>Campe√£o</div>
              <div>{steps}</div>
            </Fragment>
          ) : (
            <Fragment>
              <div style={{marginBottom: 5}}>Passos</div>
              <div>{steps}</div>
            </Fragment>
          )}
        </div>
        <div className="game">
          <div className="game-board">
            <Board
              squares={squares}
              onClick={() => w ? false : onClickSquare}
            />
          </div>
        </div>
        <div style={{marginTop: 10}}>
          <button onClick={() => onReset()}>Reiniciar</button>
          {/*<button onClick={() => onSaveAndExit(squares)}>Salvar e Sair</button>*/}
          <button onClick={() => onExit()}>Sair</button>
        </div>
      </div>
    );
  }
}

export default Game;
